sources.game.scss:
@use "modules_scss/_colors.scss" as c;
@use "modules_scss/fonts" as t;

*,
*::before,
*::after {
  margin: 0;
  margin: 0;
  padding: 0;
  box-sizing: border-box;
  overflow-x: hidden;
}

@keyframes moveSnowBall {
  0% {
    transform: translateY(5vw);
  }

  50% {
    transform: translateY(0vw);
  }
  100% {
    transform: translateY(5vw);
  }
}

@media only screen and (min-width: 901px) {
  body {
    max-height: 100%;
    max-width: 100%;

    background-color: c.$background-color;
  }

  .header {
    display: flex;
    justify-content: center;
    &__title {
      margin-top: 5vh;
      margin-bottom: 10vh;
      text-align: center;
      font: t.$headline-font;
      color: c.$snowman-color;
      max-width: 80vw;
    }
  }
  .main {
    display: flex;
    justify-content: center;
    &__snowball-wrapper-link,
    .snowball-wrapper-link {
      display: flex;
      justify-content: center;
      &__snowball-wrapper,
      .snowball-wrapper {
        display: flex;
        justify-content: center;

        &__snowball-cta {
          margin-top: 15vw;
          position: absolute;
          z-index: 10;
          color: c.$background-color;
          font: t.$h3-font;
          cursor: pointer;
          text-align: center;
        }

        &__snowball {
          cursor: pointer;
          margin-bottom: 8vw;
          height: 30vw;
          animation: moveSnowBall 2s infinite ease-in-out;
        }
      }
    }
  }
  .footer {
    &__rules-wrapper,
    .rules-wrapper {
      &__rules {
        font: t.$p-font;
        color: c.$p-font-color;
        font-size: 2.5vw;
        margin: 0 5vw 5vw 5vw;
        text-align: center;
      }
    }
  }
}

@keyframes moveSnowBall {
  0% {
    transform: translateY(5vw);
  }

  50% {
    transform: translateY(0vw);
  }
  100% {
    transform: translateY(5vw);
  }
}

