  body {
    max-height: 100%;
    max-width: 100%;

    background-color: c.$background-color;
    overflow: hidden;
  }

  .main {
    height: 100vh;
    width: 100vw;
    display: flex;
    justify-content: center;
    flex-direction: column;
    overflow: hidden;
    position: relative;

    &__text-info {
      font-family: t.$h3-font;
      color: white;
    }

    &__first-switch-wrapper,
    .first-switch-wrapper {
      height: 100vh;
      width: 100vw;
      background-color: transparent;

      &__first-switch {
        height: 30vw;
        width: auto;
        margin: 20vw 40vw 0 15vw;
      }
      &__second-switch {
