        margin-bottom: 2vw;
      }
    }
    &__throw-mechanics-wrapper,
    .throw-mechanics-wrapper {
      width: 100vw;
      height: auto;
      display: flex;
      flex-direction: column;
      justify-content: center;
      background-color: transparent;
      position: absolute;
      z-index: 100;

      &__directions,
      .directions {
        background-color: transparent;

        display: flex;
        justify-content: center;
        &__arrows,
        .arrows {
          background-color: transparent;
          margin-bottom: 30vw;
          position: relative;
          &__arrow {
            padding: 5vw;
          }
        }
      }
      &__snowball {
        height: 20vw;

        width: auto;
        background-color: transparent;
        animation: snowball-animation 1.2s ease-out 1;
        position: absolute;

        margin: 15vw 50vw 15vw 50vw;
        transform: translateX(-10vw);
        z-index: 100;
      }
    }
  }

  @keyframes snowball-animation {
