sources.main.scss:
@use "modules_scss/_colors.scss" as c;
@use "modules_scss/fonts" as t;

*,
*::before,
*::after {
  margin: 0;
  margin: 0;
  padding: 0;
  box-sizing: border-box;
  overflow: hidden;
}

body {
  max-height: 100%;
  max-width: 100%;

  background-color: c.$background-color;
}

@media (min-width: 901px) {
  .header {
    &__logo-wrapper,
    .logo-wrapper {
      &__logo {
        width: 30vw;
        height: auto;
        margin: 5vw;
        margin-top: 15vw;
        margin-left: 15vw;
        z-index: 10;
        position: absolute;
        background-color: transparent;
      }
      &__play-btn {
        width: 15vw;
        height: 15vw;
        position: absolute;
        z-index: 20;
        margin-left: 20vw;

        margin-top: 30vw;
        background-color: transparent;
        border: 1vw solid c.$snowman-color;
        font-family: t.$headline-font;
        padding: 2vw 3vw;
        border-radius: 80vw;
        font-size: 1rem;
        color: c.$snowman-color;
        cursor: pointer;
      }
    }
  }
  .main {
    &__snowman-wrapper,
    .snowman-wrapper {
      &__snowman {
        // z-index: 120;
        width: 200vw;
        margin-left: -53vw;
